const oneWeekAgo = new Date();
oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
const addedAfter = oneWeekAgo.toISOString();

// Produtos monitorados
const vendors = [
  "Fortigate", "Fortinet",
  "Palo Alto", "PaloAlto",
  "Microsoft", "Windows", "Azure", "M365",
  "Mimecast",
  "Netskope",
  "Sentinel One", "SentinelOne",
  "Exabean",
  "N8N"
];

return [{
  json: {
    addedAfter,
    vendors,
    collectionId: "{{COLLECTION_ID}}" // Voc√™ precisa descobrir o ID correto
  }
}];

const items = $input.all();
const vendors = $('Prepara Par√¢metros').first().json.vendors;

const filtered = [];

for (const item of items) {
  if (!item.json.objects) continue;

  for (const obj of item.json.objects) {
    // Buscar men√ß√µes aos vendors
    const text = JSON.stringify(obj).toLowerCase();

    const matchedVendors = vendors.filter(v => 
      text.includes(v.toLowerCase())
    );

    if (matchedVendors.length > 0) {
      filtered.push({
        json: {
          ...obj,
          matchedVendors
        }
      });
    }
  }
}

return filtered;

