const items = $input.all();

const grouped = {
  vulnerabilities: [],
  indicators: [],
  malware: [],
  threatActors: [],
  ttps: []
};

for (const item of items) {
  const obj = item.json;

  switch(obj.type) {
    case 'vulnerability':
      grouped.vulnerabilities.push(obj);
      break;
    case 'indicator':
      grouped.indicators.push(obj);
      break;
    case 'malware':
      grouped.malware.push(obj);
      break;
    case 'threat-actor':
      grouped.threatActors.push(obj);
      break;
    case 'attack-pattern':
      grouped.ttps.push(obj);
      break;
  }
}

let message = "üìä **Relat√≥rio Semanal - Threat Intelligence** üìä\n\n";
message += `Data: ${new Date().toLocaleDateString('pt-BR')}\n`;
message += `Per√≠odo: √öltimos 7 dias\n\n`;
message += "---\n\n";

// Vulnerabilidades
if (grouped.vulnerabilities.length > 0) {
  message += `üî¥ **Vulnerabilidades (${grouped.vulnerabilities.length})**\n\n`;
  grouped.vulnerabilities.slice(0, 10).forEach(v => {
    message += `‚Ä¢ **${v.name || v.id}**\n`;
    message += `  Produtos: ${v.matchedVendors.join(', ')}\n`;
    if (v.external_references) {
      const cve = v.external_references.find(r => r.source_name === 'cve');
      if (cve) message += `  CVE: ${cve.external_id}\n`;
    }
    message += "\n";
  });
  message += "\n";
}

