  grouped.indicators.slice(0, 15).forEach(i => {
    message += `‚Ä¢ ${i.pattern || i.name || i.id}\n`;
    message += `  Produtos: ${i.matchedVendors.join(', ')}\n\n`;
  });
  message += "\n";
}

// Malware
if (grouped.malware.length > 0) {
  message += `‚ò†Ô∏è **Malware (${grouped.malware.length})**\n\n`;
  grouped.malware.slice(0, 10).forEach(m => {
    message += `‚Ä¢ **${m.name || m.id}**\n`;
    message += `  Produtos: ${m.matchedVendors.join(', ')}\n\n`;
  });
  message += "\n";
}

// Threat Actors
if (grouped.threatActors.length > 0) {
  message += `üë§ **Threat Actors (${grouped.threatActors.length})**\n\n`;
  grouped.threatActors.slice(0, 10).forEach(t => {
    message += `‚Ä¢ **${t.name || t.id}**\n`;
    if (t.aliases) message += `  Aliases: ${t.aliases.join(', ')}\n`;
    message += `  Produtos: ${t.matchedVendors.join(', ')}\n\n`;
  });
  message += "\n";
}

// TTPs
if (grouped.ttps.length > 0) {
  message += `üéØ **TTPs / MITRE ATT&CK (${grouped.ttps.length})**\n\n`;
  grouped.ttps.slice(0, 10).forEach(ttp => {
    message += `‚Ä¢ **${ttp.name || ttp.id}**\n`;
    if (ttp.external_references) {
      const mitre = ttp.external_references.find(r => r.source_name === 'mitre-attack');
      if (mitre) message += `  MITRE: ${mitre.external_id}\n`;
